package result;

public class RegisterResult {
    /**
     * A String containing the value of an AuthToken created upon a successful registration attempt.
     */
    private String authToken;

    /**
     * A String containing the username of the newly created User object.
     */
    private String userName;

    /**
     * A String creating the personID generated by the system for the newly created User in the family map.
     */
    private String personID;

    /**
     * A boolean containing the result of the RegisterService class, true if successful and false if failed.
     */
    private boolean success;

    /**
     * A String containing an error message upon a failed registration attempt.
     */
    private String message;

    /**
     * The constructor for failed instances of the RegisterResult class that sets the message and success fields.
     *
     * @param message An error message reporting the reason for the registration failure.
     * @param success The boolean indicating a failed registration. Should be false.
     */
    public RegisterResult(String message, boolean success) {
        this.message = message;
        this.success = success;
    }

    /**
     * The constructor for successful instances of the RegisterResult class that sets the authToken, username, personID,
     * and success fields.
     *
     * @param authToken The value of the AuthToken that was created when this User successfully registered.
     * @param username The username given by the User that was accepted and stored by the server.
     * @param personID The ID for the Person object created for the new User of the family map.
     * @param success The boolean indicating a successful registration. Should be true.
     */
    public RegisterResult(String authToken, String username,
                   String personID, boolean success) {
        this.authToken = authToken;
        this.userName = username;
        this.personID = personID;
        this.success = success;
    }


    public String getAuthToken() {
        return authToken;
    }

    public String getUsername() {
        return userName;
    }

    public String getPersonID() {
        return personID;
    }

    public boolean isSuccess() {
        return success;
    }

    public String getMessage() {
        return message;
    }
}
